  handleDayInputChange: (id: string, value: string | undefined) => void;
  handleDayInputValue: (value: string) => void;
  dayInputValue: string | undefined;
  currentDay: string;
  placeholderValue: string;
  dayOfWeek: DayOfWeek;
}

