  }, [dayInputValue]);

  // const onChange = (e) => {
  //   setDayInputValue(e.target.value);
  //   // handleDayInputChange(dayOfWeek.dayId, dayInputValue);
  // };
  return (
    <div>
      <StyledInputContainer currentDay={currentDay}>
