import React, { useEffect, useState } from "react";
import { DayOfWeek } from "../../Types/EmployeeType";
import { StyledInputContainer, StyledITextnput } from "./styled";

interface Props {
  // handleDayInputChange: (id: string, e: string) => void;
  handleDayInputChange: (value: string) => void;
  handleDayInputClick: (id: string) => void;
  // dayInputValue: string | undefined;
  currenyDay: string;
  placeholderValue: string;
  dayOfWeek: DayOfWeek;
}

export default function DayInput({
  handleDayInputChange,
  handleDayInputClick,
  currenyDay,
  placeholderValue,
  dayOfWeek,
}: // dayInputValue,
Props) {
  const [dayInputValue, setDayInputValue] = useState<string>("");

  useEffect(() => {
    handleDayInputChange(dayInputValue);
  }, [dayInputValue]);

  return (
    <div>
      <StyledInputContainer currentDay={currenyDay}>
        <StyledITextnput
          type="text"
          value={dayInputValue}
          onChange={(e) => {
            setDayInputValue(e.target.value);
          }}
          onClick={handleDayInputClick(dayOfWeek.dayId)}
          placeholder={placeholderValue}
          maxLength="2"
        />
      </StyledInputContainer>
    </div>
  );
}
